      private void initCustomComponents() {
        setTitle("Vendas Realizadas");
        setDefaultCloseOperation(javax.swing.WindowConstants.DISPOSE_ON_CLOSE);
        setSize(400, 300);
        setLocationRelativeTo(null);

        
        tabelaVendas = new JTable();
        scrollPane = new JScrollPane(tabelaVendas);
        add(scrollPane);

        
        tabelaVendas.setModel(new DefaultTableModel(
            new Object[][] {},
            new String[] {
                "ID", "Nome", "Valor", "Status"
            }
        ));

       
        setLayout(new java.awt.BorderLayout());
        add(scrollPane, java.awt.BorderLayout.CENTER);
    }
  private void listarVendas() {
        try {
            ProdutosDAO produtosdao = new ProdutosDAO();
            DefaultTableModel model = (DefaultTableModel) tabelaVendas.getModel();
            model.setNumRows(0);
            
            // Obt√©m a lista de produtos vendidos
            ArrayList<ProdutosDTO> listagem = produtosdao.listarProdutosVendidos();
            
            for (ProdutosDTO produto : listagem) {
                model.addRow(new Object[]{
                    produto.getId(),
                    produto.getNome(),
                    produto.getValor(),
                    produto.getStatus()
                });
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Erro ao listar vendas: " + e.getMessage());
        }
    }

